    // UI State
    list        list.Model
    preview     viewport.Model
    filter      textinput.Model

    // Navigation
    focused     FocusedPane
    keyMap      ScriptsKeyMap
}

type Script struct {
    Name        string
    Path        string
    Description string
    Category    string
    Tags        []string
    Executable  bool
    LastRun     time.Time
}
```

#### CommandPalette
```go
type CommandPalette struct {
    // Input
    input       textinput.Model

    // Results
    commands    []Command
    filtered    []Command
    selected    int

    // State
    isActive    bool
    keyMap      PaletteKeyMap
}

type Command struct {
    Name        string
    Description string
    Action      func() tea.Cmd
    Category    string
    KeyBinding  string
}
```

#### SettingsPanel
```go
type SettingsPanel struct {
    // Settings groups
    groups      []SettingsGroup
    active      int

    // Current editing
    editor      textinput.Model
    editing     bool

    // State
    keyMap      SettingsKeyMap
}

type SettingsGroup struct {
    Name     string
    Settings []Setting
}

type Setting struct {
    Key         string
    Value       interface{}
    Type        SettingType
    Description string
    Validator   func(interface{}) error
}
```

#### SnippetsCollection
```go
type SnippetsCollection struct {
    // Data
    snippets    []Snippet
    categories  []string

    // UI
    list        list.Model
    preview     viewport.Model
