package scripts

import (
	"os"
	"path/filepath"
	"strings"
)

// Script represents a script file
type Script struct {
	Name        string
	Path        string
	Description string
	Executable  bool
}

// Manager handles script discovery and execution
type Manager struct {
	scriptPaths []string
	scripts     []Script
}

// NewManager creates a new script manager
func NewManager(scriptPaths []string) *Manager {
	return &Manager{
		scriptPaths: scriptPaths,
		scripts:     make([]Script, 0),
	}
}

// Scan discovers scripts in configured paths
func (m *Manager) Scan() error {
	m.scripts = make([]Script, 0)

	for _, scriptPath := range m.scriptPaths {
		err := filepath.Walk(scriptPath, func(path string, info os.FileInfo, err error) error {
			if err != nil {
				return nil // Skip inaccessible files
			}

			if info.IsDir() {
				return nil
			}

			// Check if file is executable or has script extension
			if m.isScript(path, info) {
				script := Script{
					Name:       info.Name(),
					Path:       path,
					Executable: m.isExecutable(info),
				}
				m.scripts = append(m.scripts, script)
			}

			return nil
		})

		if err != nil {
			continue // Skip paths that can't be scanned
		}
	}

	return nil
}

// GetScripts returns discovered scripts
func (m *Manager) GetScripts() []Script {
	return m.scripts
}

// isScript checks if a file should be considered a script
func (m *Manager) isScript(path string, info os.FileInfo) bool {
	ext := strings.ToLower(filepath.Ext(path))
