#!/usr/bin/env bash
use_skogai_venv() {
  # source .skogai/bin/activate
  export SKOGAI_PROMPT_PREFIX=$(basename "$(pwd)")
  export SKOGAI_VENV_BIN=$SKOGAI_VENV+"/bin"
  export SKOGAI_VENV_BIN_ACTIVATE=$SKOGAI_VENV_BIN+"/activate"

  _skogai_venv_on_enter() {
    _skogai_venv_source
    _skogai_prompt_update_venv
    _skogai_prompt_set_venv_prefix
  }
  _skogai_venv_source_activate() {
    source SKOGAI_VENV_BIN_ACTIVATE
  }
  _skogai_prompt_set_venv_prefix() {
    PS1="$SKOGAI_PROMPT_PREFIX$PS1"
  }

  if [[ -n "$SKOGAI_VENV" ]]; then
    _skogai_venv_on_enter
  fi
}
