#!/usr/bin/env python3
"""
Test script to demonstrate JSON parsing and manipulation.
"""
import json
import sys

def main(args=None):
    """Parse JSON input or create JSON output."""
    if args is None or len(args) == 0:
        print(json.dumps({"status": "ready", "message": "No args provided"}))
    elif args[0] == "parse":
        # Parse JSON from second argument
        if len(args) > 1:
            try:
                data = json.loads(args[1])
                print(f"Parsed: {data}")
            except json.JSONDecodeError as e:
                print(f"JSON Error: {e}")
        else:
            print("Need JSON string to parse")
    elif args[0] == "create":
        # Create JSON with remaining args as list
        output = {"items": args[1:], "count": len(args[1:])}
        print(json.dumps(output))
    else:
        # Echo back all arguments
        print(f"Got args: {' '.join(args)}")

if __name__ == "__main__":
    main(sys.argv[1:])
