#!/usr/bin/env python3
"""Count lines in files."""
import sys
import json
import os

def main(args=None):
    if not args:
        print(json.dumps({"error": "No files specified"}))
        return

    results = {}
    total = 0

    for filepath in args:
        if os.path.exists(filepath) and os.path.isfile(filepath):
            try:
                with open(filepath, 'r') as f:
                    lines = len(f.readlines())
                    results[filepath] = lines
                    total += lines
            except Exception as e:
                results[filepath] = f"Error: {str(e)}"
        else:
            results[filepath] = "Not found"

    output = {
        "files": results,
        "total": total,
        "count": len(results)
    }
    print(json.dumps(output))

if __name__ == "__main__":
    main(sys.argv[1:])
EOF
