#!/usr/bin/env python3
"""Process piped input with various operations."""
import sys
import json

def main(args=None):
    # Check if data is being piped in
    if not sys.stdin.isatty():
        piped_input = sys.stdin.read().strip()
    else:
        piped_input = None

    if not args:
        # No args - just process pipe
        if piped_input:
            result = {
                "input": piped_input,
                "length": len(piped_input),
                "words": len(piped_input.split()),
                "lines": len(piped_input.splitlines())
            }
        else:
            result = {"error": "No input provided"}
    elif args[0] == "upper":
        result = piped_input.upper() if piped_input else "NO INPUT"
    elif args[0] == "lower":
        result = piped_input.lower() if piped_input else "no input"
    elif args[0] == "reverse":
        result = piped_input[::-1] if piped_input else ""
    elif args[0] == "count":
        if piped_input:
            result = {"chars": len(piped_input), "tokens": len(piped_input) // 4}
        else:
            result = {"chars": 0, "tokens": 0}
    else:
        result = {"operation": args[0], "input": piped_input}

    # Output as JSON if dict, otherwise as string
    if isinstance(result, dict):
        print(json.dumps(result))
    else:
        print(result)

if __name__ == "__main__":
    main(sys.argv[1:])
EOF
skogcli script code pipe-processor --file /tmp/pipe_processor.py

