name: Claude Auto-Merge

on:
  issue_comment:
    types: [created]
  pull_request_review:
    types: [submitted]
  check_suite:
    types: [completed]

permissions:
  contents: write
  pull-requests: write
  issues: write

jobs:
  claude-automerge:
    if: |
      (github.event_name == 'issue_comment' && contains(github.event.comment.body, '@claude merge')) ||
      (github.event_name == 'pull_request_review' && github.event.review.state == 'approved' && contains(github.event.review.body, '@claude merge')) ||
      (github.event_name == 'check_suite' && github.event.check_suite.conclusion == 'success')
    runs-on: ubuntu-latest

    steps:
      - name: Checkout repository
        uses: actions/checkout@v4
        with:
          token: ${{ secrets.GITHUB_TOKEN }}

      - name: Get PR Details
        id: pr_details
        uses: actions/github-script@v7
        with:
          script: |
            let pr_number;

            if (context.eventName === 'issue_comment') {
              pr_number = context.issue.number;
            } else if (context.eventName === 'pull_request_review') {
              pr_number = context.payload.pull_request.number;
            } else if (context.eventName === 'check_suite') {
              // Find associated PR
              const pulls = await github.rest.pulls.list({
                owner: context.repo.owner,
                repo: context.repo.repo,
                head: `${context.repo.owner}:${context.payload.check_suite.head_branch}`,
                state: 'open'
              });
              if (pulls.data.length > 0) {
                pr_number = pulls.data[0].number;
              }
            }

            if (!pr_number) {
              console.log('No PR found');
              return null;
            }

            const pr = await github.rest.pulls.get({
              owner: context.repo.owner,
              repo: context.repo.repo,
              pull_number: pr_number
            });

            return {
              number: pr_number,
              mergeable: pr.data.mergeable,
              mergeable_state: pr.data.mergeable_state,
              head_sha: pr.data.head.sha,
              base_branch: pr.data.base.ref,
              labels: pr.data.labels.map(l => l.name)
            };

      - name: Check Merge Criteria
        id: check_merge
        if: steps.pr_details.outputs.result != 'null'
        uses: actions/github-script@v7
        with:
          script: |
            const pr_data = ${{ steps.pr_details.outputs.result }};

            if (!pr_data) {
              console.log('No PR data available');
              return false;
            }

            // Check if PR is mergeable
            if (!pr_data.mergeable) {
              console.log('PR has conflicts');
              return false;
            }

            // Check CI status
            const status = await github.rest.repos.getCombinedStatusForRef({
              owner: context.repo.owner,
              repo: context.repo.repo,
              ref: pr_data.head_sha
            });

            if (status.data.state !== 'success') {
              console.log(`CI status is ${status.data.state}`);
              return false;
            }

            // Check for required reviews
            const reviews = await github.rest.pulls.listReviews({
              owner: context.repo.owner,
              repo: context.repo.repo,
              pull_number: pr_data.number
            });

            const approved = reviews.data.some(r => r.state === 'APPROVED');
            if (!approved && !pr_data.labels.includes('claude-approved')) {
              console.log('PR not approved');
              return false;
            }

            // Check for do-not-merge label
            if (pr_data.labels.includes('do-not-merge') || pr_data.labels.includes('wip')) {
              console.log('PR has blocking labels');
              return false;
            }

            return true;

      - name: Auto-merge PR
        if: steps.check_merge.outputs.result == 'true'
        uses: actions/github-script@v7
        with:
          script: |
            const pr_data = ${{ steps.pr_details.outputs.result }};

            try {
              // Add auto-merge comment
              await github.rest.issues.createComment({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: pr_data.number,
                body: `ü§ñ **Claude Auto-Merge**\n\n‚úÖ All checks passed!\n‚úÖ PR approved\n‚úÖ No conflicts\n\nMerging now...`
              });

              // Perform the merge
              const merge_result = await github.rest.pulls.merge({
                owner: context.repo.owner,
                repo: context.repo.repo,
                pull_number: pr_data.number,
                merge_method: 'squash',
                commit_title: `Auto-merge PR #${pr_data.number}`,
                commit_message: `Automatically merged by Claude after all checks passed\n\nCo-authored-by: Claude <claude-bot@anthropic.com>`
              });

              if (merge_result.data.merged) {
                console.log(`Successfully merged PR #${pr_data.number}`);

                // Delete branch if it's not protected
                if (pr_data.base_branch !== 'main' && pr_data.base_branch !== 'master') {
                  try {
                    await github.rest.git.deleteRef({
                      owner: context.repo.owner,
                      repo: context.repo.repo,
                      ref: `heads/${pr_data.head_branch}`
                    });
                    console.log('Branch deleted');
                  } catch (e) {
                    console.log('Could not delete branch:', e.message);
                  }
                }
              }
            } catch (error) {
              console.error('Merge failed:', error.message);

              await github.rest.issues.createComment({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: pr_data.number,
                body: `‚ùå **Auto-merge Failed**\n\nError: ${error.message}\n\nPlease merge manually or fix the issue and try again.`
              });
            }

      - name: Report Status
        if: steps.check_merge.outputs.result == 'false' && steps.pr_details.outputs.result != 'null'
        uses: actions/github-script@v7
        with:
          script: |
            const pr_data = ${{ steps.pr_details.outputs.result }};

            await github.rest.issues.createComment({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: pr_data.number,
              body: `‚ö†Ô∏è **Cannot Auto-merge**\n\nPlease ensure:\n- ‚úì All CI checks are passing\n- ‚úì PR has been approved\n- ‚úì No merge conflicts\n- ‚úì No blocking labels (do-not-merge, wip)\n\nRerun with \`@claude merge\` when ready.`
            });
