#!/usr/bin/env bash

# @arg name![`_choice_wrappers`] script name
main() {
  echo "You selected wrapper: $argc_name"
}

_choice_wrappers() {
  cat /tmp/wrappers.txt
}
command eval "$(argc --argc-eval "$0" "$@")"

#!/usr/bin/env bash
set -e

# @describe test
# @option --foo!

main() {
    ( set -o posix ; set ) | grep ^argc_
}

eval "$(argc --argc-eval "$0" "$@")"

/**
 * test
 * @typedef {Object} Args
 * @property {string} foo - 
 * @param {Args} args
 */
exports.run = function (args) {
  console.log(args);
}

#!/usr/bin/env bash
set -e

# @describe my desc
# @option --foo bar! baz+ qux*

main() {
    ( set -o posix ; set ) | grep ^argc_
}

eval "$(argc --argc-eval "$0" "$@")"

/**
 * my desc
 * @typedef {Object} Args
 * @property {string[]} [foo bar! baz+ qux] - 
 * @param {Args} args
 */
exports.run = function (args) {
  console.log(args);
}

/**
 * my desc
 * @typedef {Object} Args
 * @property {string} foo - 
 * @property {string} bar - 
 * @property {string[]} baz - 
 * @property {string[]} [qux] - 
 * @param {Args} args
 */
exports.run = function (args) {
  console.log(args);
}

content.text:
OH! you just gave me the COMPLETE PATTERN LIBRARY!

now i see it - here are my 20 tools with **actual dynamic validation functions**:

```bash
# 1. Smart file reader - recent changes
# @cmd Read recently modified files
# @arg file![`_recent_files`]
read-recent() { cat "$argc_file"; }
_recent_files() { 
  git status --short | awk '{print $2}'
  git diff --name-only HEAD~5..HEAD
}

# 2. Test runner - only failing
# @cmd Run specific failing test
# @arg test[`_failing_tests`]
test-failing() { pytest "$argc_test"; }
_failing_tests() {
  pytest --co -q 2>/dev/null | grep "::" || echo "all-passing"
}

# 3. Branch checkout - validated
# @cmd Checkout branch safely
# @arg branch![`_git_branches`]
git-checkout() { git checkout "$argc_branch"; }
_git_branches() { git branch -a | sed 's/^[* ]*//' | sed 's/remotes\/origin\///' | sort -u; }

# 4. Container exec - OS-aware
# @cmd Execute in container
# @arg container![`_running_containers`]
# @arg command![`_container_commands`]
container-exec() { docker exec "$argc_container" "$argc_command"; }
_running_containers() { docker ps --format "{{.Names}}"; }
_container_commands() {
  # Based on selected container's OS!
  docker exec "$argc_container" which bash sh zsh 2>/dev/null
}

# 5. Service restart - managed only
# @cmd Restart development services
# @arg service![`_dev_services`]
service-restart() { systemctl --user restart "$argc_service"; }
_dev_services() {
  systemctl --user list-units "skogai-*.service" --all | 
  grep "\.service" | awk '{print $1}' | sed 's/.service$//'
}

# 6. PR review - current PRs
# @cmd Review pull request
# @arg pr![`_open_prs`]
pr-review() { gh pr checkout "$argc_pr"; }
_open_prs() { 
  gh pr list --json number,title --jq '.[] | "\(.number):\(.title)"'
}

# 7. Session resume - context-aware
# @cmd Resume Claude session
# @option --session[`_my_sessions`]
session-resume() { claude --resume "$argc_session"; }
_my_sessions() {
  ls ~/.claude/sessions/*.json 2>/dev/null | 
  xargs -n1 basename | sed 's/.json$//' | head -10
}

# 8. Function jump - codebase functions
# @cmd Jump to function definition
# @arg function![`_functions`]
function-goto() { vim "+/$argc_function"; }
_functions() {
  rg "^(def|function|class) (\w+)" -o --no-filename |
  awk '{print $2}' | sort -u
}

# 9. MCP tool call - available tools
# @cmd Call MCP tool directly
# @arg tool![`_mcp_tools`]
mcp-call() { echo "calling $argc_tool"; }
_mcp_tools() {
  curl -s http://localhost:8808/tools 2>/dev/null |
  jq -r '.[].name' || echo "read"
}

# 10. Environment variable set - current vars
# @cmd Set/update environment variable  
# @arg var[`_env_vars`]
env-set() { export "$argc_var=$argc_value"; }
_env_vars() { printenv | cut -d= -f1 | grep "^SKOGAI_\|^LLM_"; }

# 11. Package update - installed only
# @cmd Update npm package
# @arg package![`_installed_packages`]
npm-update() { npm update "$argc_package"; }
_installed_packages() {
  jq -r '.dependencies,.devDependencies | keys[]?' package.json 2>/dev/null
}

# 12. Log tail - services + containers
# @cmd Tail logs from service
# @arg source![`_log_sources`]
log-tail() { journalctl -u "$argc_source" -f; }
_log_sources() {
  systemctl --user list-units --type=service --state=running | grep "\.service" | awk '{print $1}'
  docker ps --format "{{.Names}}"
}

# 13. Git fixup - recent commits
# @cmd Create fixup commit
# @arg commit![`_recent_commits`]
git-fixup() { git commit --fixup="$argc_commit"; }
_recent_commits() {
  git log --oneline -20 | awk '{print $1":"substr($0,index($0,$2))}'
}

# 14. K8s exec - running pods
# @cmd Execute in kubernetes pod
# @arg pod![`_k8s_pods`]
k8s-exec() { kubectl exec -it "$argc_pod" -- bash; }
_k8s_pods() { kubectl get pods -o name | sed 's|pod/||'; }

# 15. Agent dispatch - available agents
# @cmd Dispatch task to agent
# @option --agent![`_available_agents`]
agent-dispatch() { skogai agent run "$argc_agent"; }
_available_agents() { ls /home/skogix/skogai/agents/*.sh | xargs -n1 basename | sed 's/.sh$//'; }

# 16. Memory search - existing tags
# @cmd Search memory by tag
# @arg tag[`_memory_tags`]
memory-search() { skogparse "[@skogai:memory:search:\"#$argc_tag\"]"; }
_memory_tags() {
  rg "^tags:" /home/skogix/skogai/docs/**/*.md | grep -oP '#\w+' | sort -u | sed 's/#//'
}

# 17. Todo complete - incomplete todos
# @cmd Mark todo as complete
# @arg todo![`_incomplete_todos`]
todo-done() { sed -i "s/- \[ \] $argc_todo/- [x] $argc_todo/" .todo/*.todo; }
_incomplete_todos() {
  grep "^- \[ \]" .todo/*.todo 2>/dev/null | sed 's/^.*- \[ \] //'
}

# 18. DB table query - actual tables
# @cmd Query database table
# @arg table![`_db_tables`]
db-query() { sqlite3 db.sqlite "SELECT * FROM $argc_table LIMIT 10"; }
_db_tables() {
  sqlite3 db.sqlite ".tables" 2>/dev/null | tr ' ' '\n'
}

# 19. Port forward - used ports
# @cmd Forward port from container
# @arg from_port![`_container_ports`]
# @arg to_port[`_available_ports`]
port-forward() { docker port-forward "$argc_from_port:$argc_to_port"; }
_container_ports() { docker ps --format "{{.Ports}}" | grep -oP '\d+(?=/tcp)'; }
_available_ports() { 
  for p in {8000..9000}; do ! lsof -i :$p &>/dev/null && echo $p; done
}

# 20. Config edit - managed configs
# @cmd Edit configuration file
# @arg config![`_config_files`]
config-edit() { $EDITOR "$argc_config"; }
_config_files() {
  find ~/.config/skogai -name "*.json" -o -name "*.yaml" 2>/dev/null
}
```

**the pattern**: every tool with dynamic validation queries CURRENT system state to show what's ACTUALLY valid RIGHT NOW!

these aren't just "nice autocomplete" - they're **impossibility barriers** that prevent invalid operations at the CLI boundary!

