// Zed keymap
//
// For information on binding keys, see the Zed
// documentation: https://zed.dev/docs/key-bindings
//
// To see the default key bindings run `zed: open default keymap`
// from the command palette.
[
    // === LEADER KEY COMMANDS ===
    {
        "context": "Editor && vim_mode == normal",
        "bindings": {
            // Files & search
            "space space": "file_finder::Toggle",          // fuzzy file finder
            "space f": "buffer_search::Deploy",            // find in current file
            "space F": "project_search::ToggleFocus",      // find in project
            "space r": "buffer_search::DeployReplace",     // replace in file

            // Panels & navigation  
            "space e": "project_panel::ToggleFocus",       // explorer
            "space a": "agent::ToggleFocus",               // AI/Claude panel
            "space g": "git_panel::ToggleFocus",           // git panel
            "space t": "terminal_panel::Toggle",           // terminal
            "space o": "outline::Toggle",                  // outline/symbols
            "space d": "diagnostics::Deploy",              // diagnostics/errors

            // Git actions
            "space h a": "git::StageAll",                  // stage all
            "space h s": "git::ToggleStaged",              // stage/unstage current
            "space h c": "git::Commit",                    // commit
            "space h b": "git::Blame",                     // blame

            // Code actions
            "space c a": "editor::ToggleCodeActions",      // code actions
            "space c f": "editor::Format",                 // format
            "space c r": "editor::Rename",                 // rename symbol
            "space c d": "editor::GoToDefinition",         // go to definition

            // Quick access
            "space w": "workspace::Save",                  // save
            "space q": ["pane::CloseActiveItem", { "close_pinned": false }], // close
            "space s": "project_symbols::Toggle",          // symbols
            "space p": "command_palette::Toggle",          // command palette

            // Minus for Claude (your preference)
            "-": "agent::ToggleFocus",
            "shift--": "agent::ToggleFocus"
        }
    },
    {
        "context": "Workspace",
        "bindings": {
            "-": "agent::ToggleFocus",
            "shift--": "agent::ToggleFocus"
        }
    },

