using System;
using System.Linq;
using System.Reflection;
using System.Text;

public class DllInspector
{
    public static void InspectDll(string dllPath)
    {
        try
        {
            Assembly assembly = Assembly.LoadFrom(dllPath);

            Console.WriteLine($"Assembly: {assembly.GetName().Name}");
            Console.WriteLine($"Version: {assembly.GetName().Version}");
            Console.WriteLine();

            foreach (Type type in assembly.GetExportedTypes())
            {
                Console.WriteLine($"Type: {type.FullName}");

                // Get constructors
                foreach (ConstructorInfo ctor in type.GetConstructors(BindingFlags.Public | BindingFlags.Instance))
                {
                    Console.WriteLine($"  Constructor: {GetMethodSignature(ctor)}");
                }

                // Get methods
                foreach (MethodInfo method in type.GetMethods(BindingFlags.Public | BindingFlags.Instance | BindingFlags.Static | BindingFlags.DeclaredOnly))
                {
                    Console.WriteLine($"  Method: {GetMethodSignature(method)}");
                }

                // Get properties
                foreach (PropertyInfo prop in type.GetProperties(BindingFlags.Public | BindingFlags.Instance | BindingFlags.Static))
                {
                    Console.WriteLine($"  Property: {prop.PropertyType.Name} {prop.Name} {{ {(prop.CanRead ? "get; " : "")}{(prop.CanWrite ? "set; " : "")}}}");
                }

                Console.WriteLine();
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error loading assembly: {ex.Message}");
        }
    }

    private static string GetMethodSignature(MethodBase method)
    {
        var sb = new StringBuilder();

        // Return type (for methods, not constructors)
        if (method is MethodInfo mi)
        {
            sb.Append($"{mi.ReturnType.Name} ");
        }

        // Method name
        sb.Append(method.Name);

        // Generic parameters
        if (method.IsGenericMethodDefinition)
        {
            sb.Append("<");
            sb.Append(string.Join(", ", method.GetGenericArguments().Select(t => t.Name)));
            sb.Append(">");
        }

        // Parameters
        sb.Append("(");
        var parameters = method.GetParameters();
        sb.Append(string.Join(", ", parameters.Select(p => $"{p.ParameterType.Name} {p.Name}")));
        sb.Append(")");

        return sb.ToString();
    }

    public static void Main(string[] args)
    {
        if (args.Length == 0)
        {
            Console.WriteLine("Usage: DllInspector.exe <path-to-dll>");
            return;
        }

        InspectDll(args[0]);
    }
}

