# PowerShell script to inspect DLLs in PartsTown project

param(
    [string]$DllPath = ".\PartsTown\bin\",
    [string]$OutputFile = "dll-signatures.txt"
)

function Get-DllSignatures {
    param([string]$AssemblyPath)

    try {
        $assembly = [System.Reflection.Assembly]::LoadFrom($AssemblyPath)
        $output = @()

        $output += "=" * 80
        $output += "Assembly: $($assembly.GetName().Name)"
        $output += "Version: $($assembly.GetName().Version)"
        $output += "=" * 80

        foreach ($type in $assembly.GetExportedTypes()) {
            if (-not $type.IsPublic) { continue }

            $output += "`nType: $($type.FullName)"
            $output += "-" * 40

            # Constructors
            $constructors = $type.GetConstructors()
            if ($constructors.Count -gt 0) {
                $output += "  Constructors:"
                foreach ($ctor in $constructors) {
                    $params = ($ctor.GetParameters() | ForEach-Object { "$($_.ParameterType.Name) $($_.Name)" }) -join ", "
                    $output += "    new $($type.Name)($params)"
                }
            }

            # Methods
            $methods = $type.GetMethods([System.Reflection.BindingFlags]::Public -bor [System.Reflection.BindingFlags]::Instance -bor [System.Reflection.BindingFlags]::Static -bor [System.Reflection.BindingFlags]::DeclaredOnly)
            if ($methods.Count -gt 0) {
                $output += "  Methods:"
                foreach ($method in $methods) {
                    $params = ($method.GetParameters() | ForEach-Object { "$($_.ParameterType.Name) $($_.Name)" }) -join ", "
                    $output += "    $($method.ReturnType.Name) $($method.Name)($params)"
                }
            }

            # Properties
            $properties = $type.GetProperties()
            if ($properties.Count -gt 0) {
                $output += "  Properties:"
                foreach ($prop in $properties) {
                    $accessors = @()
                    if ($prop.CanRead) { $accessors += "get" }
                    if ($prop.CanWrite) { $accessors += "set" }
                    $output += "    $($prop.PropertyType.Name) $($prop.Name) { $($accessors -join '; ') }"
                }
            }
        }

        return $output
    }
    catch {
        return @("Error processing $AssemblyPath : $_")
    }
}

# Main execution
$allOutput = @()

if (Test-Path $DllPath) {
    $dllFiles = Get-ChildItem -Path $DllPath -Filter "*.dll" -File

    foreach ($dll in $dllFiles) {
        Write-Host "Processing $($dll.Name)..."
        $signatures = Get-DllSignatures -AssemblyPath $dll.FullName
        $allOutput += $signatures
        $allOutput += "`n"
    }

    # Write to file
    $allOutput | Out-File -FilePath $OutputFile -Encoding UTF8
    Write-Host "Output written to $OutputFile"
}
else {
    Write-Host "Path not found: $DllPath"
    Write-Host "Please build the project first: msbuild PartsTown.sln"
}

