using Microsoft.VisualStudio.TestTools.UnitTesting;

namespace PartsTown.Tests
{
    /// <summary>
    /// Real-world approach: Use unit tests to document and verify API surfaces
    /// </summary>
    [TestClass]
    public class AssemblyInspectionTests
    {
        [TestMethod]
        public void DocumentPublicApiSurface()
        {
            // This is how many real projects track their public API
            var assembly = Assembly.Load("PartsTown");
            var publicTypes = assembly.GetExportedTypes()
                .Where(t => t.IsPublic && !t.IsNested)
                .OrderBy(t => t.FullName);

            var apiSurface = new List<string>();

            foreach (var type in publicTypes)
            {
                apiSurface.Add($"Type: {type.FullName}");

                // Document public methods
                var methods = type.GetMethods(BindingFlags.Public | BindingFlags.Instance | BindingFlags.Static)
                    .Where(m => !m.IsSpecialName) // Skip property getters/setters
                    .OrderBy(m => m.Name);

                foreach (var method in methods)
                {
                    var parameters = string.Join(", ",
                        method.GetParameters().Select(p => $"{p.ParameterType.Name} {p.Name}"));
                    apiSurface.Add($"  {method.ReturnType.Name} {method.Name}({parameters})");
                }
            }

            // In real projects, you'd compare this to a baseline file
            // to detect breaking changes
            System.IO.File.WriteAllLines("api-surface.txt", apiSurface);

            // Assert API hasn't changed unexpectedly
            Assert.IsTrue(apiSurface.Count > 0, "API surface should not be empty");
        }

        [TestMethod]
        public void VerifyNoDependencyOnObsoleteTypes()
        {
            var assembly = Assembly.Load("PartsTown");

            foreach (var type in assembly.GetTypes())
            {
                // Check for obsolete attribute usage
                var obsoleteAttr = type.GetCustomAttribute<ObsoleteAttribute>();
                if (obsoleteAttr != null)
                {
                    Console.WriteLine($"Warning: {type.Name} is marked obsolete: {obsoleteAttr.Message}");
                }
            }
        }
    }
}

