                var interfaces = service.GetInterfaces();
                foreach (var @interface in interfaces)
                {
                    // Register: container.Register(@interface, service);
                    Console.WriteLine($"Would register: {@interface.Name} -> {service.Name}");
                }
            }
        }
    }
