{
    "name": "PartsTown Dev Container",
    "dockerComposeFile": "docker-compose.yml",
    "service": "devtools",
    "workspaceFolder": "/workspace",

    // Configure VS Code settings
    "customizations": {
        "vscode": {
            "settings": {
                "terminal.integrated.defaultProfile.linux": "bash",
                "dotnet.defaultSolution": "PartsTown.sln",
                "omnisharp.useModernNet": false,
                "omnisharp.useGlobalMono": "always",
                "omnisharp.monoPath": "/usr/bin/mono",
                "files.watcherExclude": {
                    "**/bin/**": true,
                    "**/obj/**": true,
                    "**/packages/**": true,
                    "**/.git/objects/**": true,
                    "**/.git/subtree-cache/**": true
                },
                "search.exclude": {
                    "**/bin": true,
                    "**/obj": true,
                    "**/packages": true,
                    "**/.skogai/.backup": true
                }
            },
            "extensions": [
                "ms-dotnettools.csharp",
                "ms-vscode.mono-debug",
                "ms-azuretools.vscode-docker",
                "ms-vscode.vscode-xml",
                "editorconfig.editorconfig",
                "streetsidesoftware.code-spell-checker",
                "yzhang.markdown-all-in-one",
                "redhat.vscode-yaml",
                "christian-kohler.path-intellisense",
                "formulahendry.dotnet"
            ]
        }
    },

    // Features to add to the container
    "features": {
        "ghcr.io/devcontainers/features/common-utils:2": {
            "installZsh": true,
            "upgradePackages": true,
            "username": "vscode",
            "userUid": "automatic",
            "userGid": "automatic"
        },
        "ghcr.io/devcontainers/features/git:1": {
            "version": "latest"
        },
        "ghcr.io/devcontainers/features/docker-in-docker:2": {
            "version": "latest",
            "moby": true
        }
    },

    // Port forwarding
    "forwardPorts": [
        8080,  // Application HTTP
        8443,  // Application HTTPS
        1433,  // SQL Server
        5000,  // Additional dev port
        5001   // Additional dev port HTTPS
    ],

    "portsAttributes": {
        "8080": {
            "label": "PartsTown App",
            "onAutoForward": "notify"
        },
        "1433": {
            "label": "SQL Server",
            "onAutoForward": "silent"
        }
    },

    // Run commands after the container is created
    "postCreateCommand": "bash -c 'chmod +x .skogai/scripts/* && nuget restore PartsTown.sln'",

    // Run commands after attaching to the container
    "postAttachCommand": "echo 'Welcome to PartsTown Dev Container! Run \"docker-compose up sqlserver\" to start SQL Server.'",

    // Environment variables
    "containerEnv": {
        "ASPNETCORE_ENVIRONMENT": "Development",
        "DOTNET_CLI_TELEMETRY_OPTOUT": "1",
        "MONO_THREADS_PER_CPU": "2000",
        "MONO_GC_PARAMS": "nursery-size=64m"
    },

    // Mount points
    "mounts": [
        // Mount docker socket for Docker-in-Docker
        "source=/var/run/docker.sock,target=/var/run/docker.sock,type=bind",
        // Persist VS Code extensions
        "source=vscode-extensions,target=/home/vscode/.vscode-server/extensions,type=volume",
        // Persist bash history
        "source=bashhistory,target=/commandhistory,type=volume"
    ],

    // User to run as
    "remoteUser": "vscode",

    // Shutdown action
    "shutdownAction": "stopCompose"
}

