﻿@echo off
setlocal

echo Deleting all bin, obj, packages, .vs, .github, app.publish, and TestResults folders...

for %%d in (bin obj packages .vs TestResults .github app.publish) do (
    echo Searching for "%%d" folders...
    for /d /r %%f in (%%d) do (
        if /i "%%~nxf"=="%%d" (
            echo Deleting: "%%f"
            rmdir /s /q "%%f"
        )
    )
)

echo Delete *.sln.DotSettings.user
for /r %%i in (*.sln.DotSettings.user) do del "%%i"

echo Delete *.csproj.user
for /r %%i in (*.csproj.user) do del "%%i"

echo Done.
endlocal

