+using System;
+using Microsoft.VisualStudio.TestTools.UnitTesting;
+using PartsTown.Tests.IoC;
+using PT.Core.Model.Interface.Service;
+using PartsTown.CustomData.Magic360;
+
+namespace PartsTown.Tests
+{
+    [TestClass]
+    public class Magic360Tests
+    {
+        [TestMethod]
+        public void TestIErrorLoggerInterface()
+        {
+            // Initialize the IoC container
+            var resolver = TestHelpers.Init();
+
+            // Get IErrorLogger to check its methods
+            var errorLogger = resolver.Resolve<IErrorLogger>();
+
+            Assert.IsNotNull(errorLogger, "IErrorLogger should be resolved");
+
+            // Try to figure out what methods it has
+            var type = errorLogger.GetType();
+            var methods = type.GetMethods();
+
+            foreach (var method in methods)
+            {
+                if (method.Name.Contains("Log"))
+                {
+                    Console.WriteLine($"Method: {method.Name}");
+                    var parameters = method.GetParameters();
+                    foreach (var param in parameters)
+                    {
+                        Console.WriteLine($"  - {param.Name}: {param.ParameterType.Name}");
+                    }
+                }
+            }
+        }
+
+        [TestMethod]
+        public void TestMagic360Repository()
+        {
+            var resolver = TestHelpers.Init();
+
+            // Try to create a Magic360ImageRepository
+            var site = new PT.Core.Model.Site { CompanyCode = "TEST" };
+            var errorLogger = resolver.Resolve<IErrorLogger>();
+
+            var repo = new Magic360ImageRepository(site, errorLogger);
+
+            Assert.IsNotNull(repo, "Repository should be created");
+
+            // Test the TestIceSchema method
+            var result = repo.TestIceSchema();
+            Console.WriteLine($"TestIceSchema result: {result}");
+        }
+    }
+}
