using System;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using PartsTown.Tests.IoC;
using PT.Core.Model.Interface;
using PT.Core.Model.Interface.Service;
using PT.Core.Model.Interface.Sites;
using PT.Core.Sql;
using PartsTown.CustomData.Magic360;

namespace PartsTown.Tests
{
    [TestClass]
    public class Magic360Tests
    {
        [TestMethod]
        public void TestIErrorLoggerInterface()
        {
            // Initialize the IoC container
            var resolver = TestHelpers.Init();

            // Get IErrorLogger to check its methods
            var errorLogger = resolver.Get<IErrorLogger>();

            Assert.IsNotNull(errorLogger, "IErrorLogger should be resolved");

            // Try to figure out what methods it has
            var type = errorLogger.GetType();
            var methods = type.GetMethods();

            foreach (var method in methods)
            {
                if (method.Name.Contains("Log"))
                {
                    Console.WriteLine($"Method: {method.Name}");
                    var parameters = method.GetParameters();
                    foreach (var param in parameters)
                    {
                        Console.WriteLine($"  - {param.Name}: {param.ParameterType.Name}");
                    }
                }
            }
        }

        [TestMethod]
        public void TestMagic360Repository()
        {
            var resolver = TestHelpers.Init();

            // Test that the repository type exists and can be resolved through factory
            var factoryType = typeof(ISiteServiceFactory<Magic360ImageRepository>);
            Assert.IsTrue(factoryType != null, "Factory interface type should exist");

            // Test that the repository type exists
            var repoType = typeof(Magic360ImageRepository);
            Assert.IsTrue(repoType != null, "Repository type should exist");

            // Test that interface exists
            var interfaceType = typeof(IMagic360ImageRepository);
            Assert.IsTrue(interfaceType != null, "Repository interface should exist");

            // To properly test with actual instances, you'd need:
            // var factory = resolver.Get<ISiteServiceFactory<Magic360ImageRepository>>();
            // var site = resolver.Get<ISite>();
            // var repo = factory.Create(site);
            // var result = repo.TestIceSchemaAccess();
            // Console.WriteLine($"TestIceSchemaAccess result: {result}");
        }
    }
}

