using PartsTown.App_Start;
using System;
using System.Web;

namespace PartsTown
{
    // Note: For instructions on enabling IIS6 or IIS7 classic mode, 
    // visit http://go.microsoft.com/?LinkId=9394801
    public class MvcApplication : System.Web.HttpApplication
    {
        private readonly PartsTownApplication App = new PartsTownApplication();

        protected void Application_Start()
        {
            App.Start();
        }
        protected void Application_End(object sender, EventArgs e)
        {
            App.Stop();
        }
        protected void Application_BeginRequest(object sender, EventArgs e)
        {
            App.ValidateRequest(HttpContext.Current);
        }
    }
}

﻿@echo off
setlocal

echo Deleting all bin, obj, packages, .vs, .github, app.publish, and TestResults folders...

for %%d in (bin obj packages .vs TestResults .github app.publish) do (
    echo Searching for "%%d" folders...
    for /d /r %%f in (%%d) do (
        if /i "%%~nxf"=="%%d" (
            echo Deleting: "%%f"
            rmdir /s /q "%%f"
        )
    )
)

echo Delete *.sln.DotSettings.user
for /r %%i in (*.sln.DotSettings.user) do del "%%i"

echo Delete *.csproj.user
for /r %%i in (*.csproj.user) do del "%%i"

echo Done.
endlocal
