using System;
using System.Linq;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using PartsTown.Tests.IoC;
using PT.TaskTool.VSTools.Projects;

namespace PartsTown.Tests
{
    [TestClass]
    public class BasicTests
    {
        [TestMethod]
        public void BasicUserTest()
        {
            var resolver = TestHelpers.Init();
            var svc = PT.OnlineBusiness.Tests.Helpers.TestHelpers.GetGenericService(resolver);

            var user = svc.Users.Get(3);
            Assert.IsNotNull(user, "User 3 not found");
        }

        [TestMethod]
        public void ValidateProjectViewFiles()
        {
            // This works as long as the main project name is the same as the beginning of this class' namespace
            var projectName = GetType().Namespace.Split('.').Reverse().Skip(1).First();
            var projectFilename = $"..\\..\\..\\..\\{projectName}\\{projectName}.csproj";
            var solutionDir = "..\\..\\..\\..";

            var file = ProjectFile.FromLocalFile(projectFilename, solutionDir, ProjectFile.FileFormatType.Old);
            var missing = file.GetMissingLocalViewFiles().ToList();

            Assert.AreEqual(0, missing.Count, $"View files not included in project:\n{string.Join("\n", missing)}");
        }

        [TestMethod]
        public void ValidateProjectContentFiles()
        {
            // This works as long as the main project name is the same as the beginning of this class' namespace
            var projectName = GetType().Namespace.Split('.').Reverse().Skip(1).First();
            var projectFilename = $"..\\..\\..\\..\\{projectName}\\{projectName}.csproj";
            var solutionDir = "..\\..\\..\\..";

            var file = ProjectFile.FromLocalFile(projectFilename, solutionDir, ProjectFile.FileFormatType.Old);
            var missing = file.GetDeletedContentFiles().ToList();

            Assert.AreEqual(0, missing.Count, $"Content files included in project that does not exist on disk:\n{string.Join("\n", missing)}");
        }
    }
}

