﻿using CsQuery.Engine.PseudoClassSelectors;
using PartsTown.CustomData;
using PartsTown.CustomData.Magic360;
using PartsTown.CustomData.Magic360.Interface;
using PartsTown.Models.Magic360;
using PT.Core.Model.General;
using PT.Core.Model.Interface;
using PT.Core.Model.Interface.Context;
using PT.Core.Model.Interface.Service;
using PT.Core.Model.Interface.Sites;
using PT.One.Controllers.Controllers;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Net;
using System.Web;
using System.Web.Mvc;
using static PT.Admin.Core.Enums.PaymentOptions;

namespace PartsTown.Controllers
{
    public class Magic360Controller : BasePageController
    {
        private readonly ISiteServiceFactory<IMagic360ImageCollection> Magic360CollectionFactory;
        public Magic360Controller(ISiteUserContext siteUserContext, IErrorLogger errorLogger,
            IRequestStorage requestStorage, IRequestContext requestContext, IRoutingContext routingContext, 
            IWebUserContext webUserContext, ISiteServiceFactory<ISiteServices> siteServices, IUserPrincipalContext userPrincipalContext, ISiteCollection sites, ISiteServiceFactory<IMagic360ImageCollection> magicCollectionFactory) : base(siteUserContext, errorLogger, requestStorage, requestContext, routingContext, webUserContext, siteServices, userPrincipalContext, sites)
        {
            Magic360CollectionFactory = magicCollectionFactory;
        }

        // GET: Magic360
        public PartialViewResult ImageView(string id)
        {
            var magicCollection = Magic360CollectionFactory.Create(GenericService.Site);
            var images = magicCollection.GetImagesForPart(id).ToList();

            var model = new ImageViewModel(id);
            GetImagesModelFromProductImage(images, ref model);


            return PartialView(model);
        }

        private void GetImagesModelFromProductImage(List<ProductImage> productImages, ref ImageViewModel model)
        {
            foreach (var productImage in productImages)
            {
                var image = new ImageModel()
                {
                    ImageUrl = productImage.Url,
                    LargeImageUrl = productImage.LargeUrl,
                    ImageType = Magic360Helper.MapDocTypeToImageType(productImage.DocTypeId)
                };

                SetPresetOptions(image);
                image.Options.IsLazyLoadEnabled = true;
                image.Options.IsFullscreenEnabled = true;
                image.Options.Magnifier.IsEnabled = true;
                image.Options.Magnifier.Shape = MagnifierShape.Circle;
                image.Options.Magnifier.Width = "80%";
                model.Images.Add(image);
            }
        }
        private void SetPresetOptions(ImageModel model)
        {
            switch (model.ImageType)
            {
                case ImageType.Single:
                    break;
                case ImageType.Twenty:
                    break;
                case ImageType.Forty:
                    break;
                case ImageType.Sixty:
                    break;
            }
        }
    }
}
