using System;
using System.Linq;
using System.Threading.Tasks;
using System.Web.Mvc;
using PT.Core.Model.IoC;

namespace PartsTown.CustomData.ErpIntegration
{
    /// <summary>
    /// Example controller showing how to use the ERP repository
    /// </summary>
    public class ErpDataController : Controller
    {
        private readonly IPartsTownErpRepository _erpRepository;
        private readonly IDependencyResolver _dependencyResolver;

        /// <summary>
        /// Constructor with dependency injection
        /// The repository will be injected automatically by the IoC container
        /// </summary>
        public ErpDataController(IPartsTownErpRepository erpRepository, IDependencyResolver dependencyResolver)
        {
            _erpRepository = erpRepository;
            _dependencyResolver = dependencyResolver;
        }

        /// <summary>
        /// Example: Get customer data from ERP
        /// </summary>
        [HttpGet]
        public ActionResult GetCustomer(string customerCode)
        {
            try
            {
                var customer = _erpRepository.GetCustomerByCode(customerCode);

                if (customer == null)
                {
                    return HttpNotFound($"Customer {customerCode} not found in ERP");
                }

                // Return customer data (would typically be mapped to a view model)
                return Json(new
                {
                    CustomerCode = customer.CustomerCode,
                    Name = customer.Name,
                    Email = customer.Email,
                    Phone = customer.Phone
                }, JsonRequestBehavior.AllowGet);
            }
            catch (Exception ex)
            {
                return new HttpStatusCodeResult(500, $"Error accessing ERP: {ex.Message}");
            }
        }

        /// <summary>
        /// Example: Get customer credit information
        /// </summary>
        [HttpGet]
        public ActionResult GetCustomerCredit(string customerCode)
        {
            try
            {
                var creditInfo = _erpRepository.GetCustomerCreditInfo(customerCode);

                if (creditInfo == null)
                {
                    return HttpNotFound($"Credit info not found for customer {customerCode}");
                }

                return Json(creditInfo, JsonRequestBehavior.AllowGet);
            }
            catch (Exception ex)
            {
                return new HttpStatusCodeResult(500, $"Error accessing ERP credit data: {ex.Message}");
            }
        }

        /// <summary>
        /// Example: Get customer orders from ERP
        /// </summary>
        [HttpGet]
        public ActionResult GetCustomerOrders(string customerCode)
        {
            try
            {
                var orders = _erpRepository.GetCustomerOrders(customerCode);

                var orderList = orders.Select(o => new
                {
                    OrderNumber = o.OrderNumber,
                    OrderDate = o.OrderDate,
                    Status = o.Status,
                    TotalAmount = o.TotalAmount
                }).ToList();

                return Json(orderList, JsonRequestBehavior.AllowGet);
            }
            catch (Exception ex)
            {
                return new HttpStatusCodeResult(500, $"Error accessing ERP orders: {ex.Message}");
            }
        }

        /// <summary>
        /// Example: Sync inventory from ERP
        /// </summary>
        [HttpPost]
        public async Task<ActionResult> SyncInventory(string[] productCodes)
        {
            try
            {
                var success = await _erpRepository.SyncInventoryFromErp(productCodes);

                if (success)
                {
                    return Json(new { success = true, message = "Inventory sync completed successfully" });
                }
                else
                {
                    return Json(new { success = false, message = "Inventory sync failed" });
                }
            }
            catch (Exception ex)
            {
                return new HttpStatusCodeResult(500, $"Error syncing inventory: {ex.Message}");
            }
        }

        /// <summary>
        /// Example: Direct ERP access using dependency resolver
        /// This shows how to dynamically resolve ERP services
        /// </summary>
        [HttpGet]
        public ActionResult GetErpData(string dataType)
        {
            try
            {
                // Dynamically resolve different ERP repositories based on data type
                switch (dataType.ToLower())
                {
                    case "customer":
                        var customerRepo = _dependencyResolver.Get<PT.Core.Model.Interface.ERP.Customer.ICustomerRepository>();
                        var customers = customerRepo.Get(new[] { "CUST001", "CUST002" });
                        return Json(customers.Select(c => c.CustomerCode), JsonRequestBehavior.AllowGet);

                    case "order":
                        var orderRepo = _dependencyResolver.Get<PT.Core.Model.Interface.ERP.SalesOrders.ISalesOrderRepository>();
                        var query = orderRepo.CreateQuery();
                        query.MaxResults = 10;
                        var orders = orderRepo.Get(query);
                        return Json(orders.Select(o => o.OrderNumber), JsonRequestBehavior.AllowGet);

                    default:
                        return HttpNotFound($"Unknown data type: {dataType}");
                }
            }
            catch (Exception ex)
            {
                return new HttpStatusCodeResult(500, $"Error accessing ERP: {ex.Message}");
            }
        }
    }
}

