using PT.Core.Model.Interface.Sites;
using PT.Core.Sql;

namespace PartsTown.CustomData.ErpIntegration
{
    /// <summary>
    /// Provides connection strings and connections to the ERP database
    /// This implementation leverages the existing Parttrap framework's connection management
    /// </summary>
    [InjectableImplementation]
    public class ErpConnectionProvider : IErpConnectionProvider
    {
        private readonly ISiteContextProvider _siteContextProvider;
        private readonly IConnectionStringProvider _connectionStringProvider;
        private readonly IErrorLogger _errorLogger;

        public ErpConnectionProvider(
            ISiteContextProvider siteContextProvider,
            IConnectionStringProvider connectionStringProvider,
            IErrorLogger errorLogger)
        {
            _siteContextProvider = siteContextProvider ?? throw new ArgumentNullException(nameof(siteContextProvider));
            _connectionStringProvider = connectionStringProvider ?? throw new ArgumentNullException(nameof(connectionStringProvider));
            _errorLogger = errorLogger ?? throw new ArgumentNullException(nameof(errorLogger));
        }

        /// <summary>
        /// Gets the ERP connection string from the current site context
        /// </summary>
        public string GetConnectionString()
        {
            try
            {
                var site = _siteContextProvider.GetCurrentSite();

                // First try to get ERP-specific connection string
                var erpConnectionString = GetErpConnectionStringFromConfig(site);
                if (!string.IsNullOrEmpty(erpConnectionString))
                    return erpConnectionString;

                // Fallback to main connection string if no ERP-specific one exists
                return _connectionStringProvider.GetConnectionString();
            }
            catch (Exception ex)
            {
                _errorLogger.LogError("Error getting ERP connection string", ex);
                throw;
            }
        }

        /// <summary>
        /// Gets connection string for a specific database/catalog
        /// </summary>
        public string GetConnectionString(string database)
        {
            if (string.IsNullOrEmpty(database))
                throw new ArgumentNullException(nameof(database));

